package javaexample;

public class Print1to100 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int startCondition = 'A'/'A';
		String endCondition = "**********";
		System.out.println("Method 1");
		for(int i=startCondition;i<=(endCondition.length())*(endCondition.length());i++)
		{
			System.out.println(i);
		}

		System.out.println("Method 2");
		int valueA = 'A';
		int valuea = 'a';
		System.out.println(valueA);
		System.out.println(valuea);
		int endCond='d';
		for(int i='A'/'A';i<=endCond;i++)
		{
			System.out.println(i);
		}
		
	}

}
